frappe.ui.form.on('Sales Order', {
    validate: function(frm) {
        if (frm.doc.items && frm.doc.items.length > 0) {
            frm.doc.items.forEach(function(row) {
                if (row.qty>100){
                 //frappe.throw(__('Quantity not allowed more then 100'));   
                 frappe.throw(
                        __("Row {0}: Quantity not allowed more than 100", [row.idx])
                    );
                }
            });
        }
    }
});

frappe.ui.form.on('Job Inward_In-House Job', {
    onload: function(frm) {
        // Agar inward_date khali ho to aaj ki date set karo
        if (!frm.doc.inward_date) {
            frm.set_value('inward_date', frappe.datetime.get_today());
        }
    },

    challan_date(frm) {
        const challanDate = frappe.datetime.str_to_obj(frm.doc.challan_date);
        const inwardDate = frappe.datetime.str_to_obj(frm.doc.inward_date);

        // Only validate if both dates exist
        if (challanDate && inwardDate && inwardDate < challanDate) {
            frappe.msgprint(__('Challan Date cannot be after Inward Date.'));
            frm.set_value('challan_date', '');
        }
    },

    validate(frm) {
        const challanDate = frappe.datetime.str_to_obj(frm.doc.challan_date);
        const inwardDate = frappe.datetime.str_to_obj(frm.doc.inward_date);

        if (challanDate && inwardDate && inwardDate < challanDate) {
            frappe.throw(__('Challan Date cannot be after Inward Date.'));
        }
    }
});

frappe.ui.form.on('Job Inward_In-House Job', {
    onload: function(frm) {
        // Warehouse aur Product Name filters set karo
        set_warehouse_query(frm);
        set_product_name_query(frm);

        // JobInward Type ka default set karo agar blank hai
        if (!frm.doc.jobinward_type) {
            frm.set_value('jobinward_type', 'Material Receipt');
        }

        // User ko change karne se roko
        frm.set_df_property('jobinward_type', 'read_only', 1);

        // Master (Cost Center) filter set karo
        set_master_query(frm);
    },

    company: function(frm) {
        set_warehouse_query(frm);
        set_master_query(frm); // Company change par Master filter update karo
    }
});

// âœ… Master field (Cost Center) filter by Company
function set_master_query(frm) {
    frm.set_query("master", function() {
        return {
            filters: {
                company: frm.doc.company
            }
        };
    });
}

function set_warehouse_query(frm) {
    // For Parent Warehouse Name
    frm.set_query('warehouse_name', () => {
        return {
            filters: {
                company: frm.doc.company
            }
        };
    });

    // For Child Table's Warehouse Name
    frm.fields_dict.table_duni.grid.get_field('warehouse_name').get_query = function(doc, cdt, cdn) {
        return {
            filters: {
                company: frm.doc.company
            }
        };
    };
}

// ðŸ”¹ Product Name filter by Product Type
function set_product_name_query(frm) {
    frm.fields_dict.table_duni.grid.get_field('product_name').get_query = function(doc, cdt, cdn) {
        const child = locals[cdt][cdn];
        return {
            filters: {
                item_group: child.product_type
            }
        };
    };
}

frappe.ui.form.on("Job Inward Item", {
    product_name: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];

        if (row.product_name) {
            frappe.db.get_value("Item", row.product_name, "stock_uom").then(r => {
                if (r.message && r.message.stock_uom) {
                    frappe.model.set_value(cdt, cdn, "unit", r.message.stock_uom);

                    // Sirf current row ka Unit disable karo
                    let grid_row = frm.fields_dict.job_inward_item.grid.get_row(cdn);
                    if (grid_row) {
                        grid_row.toggle_editable("unit", false);
                    }
                }
            });
        }
    },

    unit: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.unit) {
            let grid_row = frm.fields_dict.job_inward_item.grid.get_row(cdn);
            if (grid_row) {
                grid_row.toggle_editable("unit", false);
            }
        }
    }
});

frappe.ui.form.on('Job Inward_In-House Job', {
    onload(frm) {
        // à¤¸à¤¿à¤°à¥à¤«à¤¼ à¤¨à¤ document à¤ªà¤° à¤¹à¥€ series generate à¤•à¤°à¥‹
        if (frm.is_new() && !frm.doc.amended_from) {
            frappe.db.get_list('Job Inward_In-House Job', {
                fields: ['jobinward_no'],
                order_by: 'creation desc',
                limit: 1
            }).then(records => {
                if (records.length) {
                    let last_no = records[0].jobinward_no || "00000/00";
                    let [num, year] = last_no.split('/');
                    let next_num = String(parseInt(num) + 1).padStart(5, '0');
                    let final_no = `${next_num}/${year}`;
                    frm.set_value('jobinward_no', final_no);
                    frm.set_df_property('jobinward_no', 'read_only', 1);
                }
            });
        }
    },
    before_save(frm) {
        set_from_series(frm);
    },
    before_submit(frm) {
        set_from_series(frm);
    }
});

function set_from_series(frm) {
    // à¤…à¤—à¤° à¤¯à¥‡ amended doc à¤¹à¥ˆ à¤¤à¥‹ à¤ªà¥à¤°à¤¾à¤¨à¤¾ à¤¹à¥€ jobinward_no à¤°à¤¹à¤¨à¥‡ à¤¦à¥‹
    if (frm.doc.amended_from) {
        frappe.db.get_value("Job Inward_In-House Job", frm.doc.amended_from, "jobinward_no")
            .then(r => {
                if (r.message && r.message.jobinward_no) {
                    frm.set_value('jobinward_no', r.message.jobinward_no);
                }
            });
    } else {
        if (frm.doc.name && !frm.doc.__islocal) {
            const final_no = build_no_from_name(frm.doc.name);
            if (final_no) {
                frm.set_value('jobinward_no', final_no);
            }
        }
    }
}

function build_no_from_name(name) {
    const m = name.match(/-(\d{2})-(\d{5})$/);
    if (m) return `${m[2]}/${m[1]}`;
    return null;
}

// ---------- CONFIG ----------
const PARENT_DOCTYPE = "Job Inward_In-House Job";
const TABLE_FIELD = "job_inward_item";
const DETAIL_TABLE_FIELD = "job_inward_item_detail";
const EXISTING_BTN_FIELDNAME = "btn_detail";
const INJECT_CLASS = "injected-detail-btn";
// -------------------------------------------------------------------------------

let current_item_reference = null;
let current_item_key = null;

frappe.ui.form.on(PARENT_DOCTYPE, {
    onload(frm) {
        frm.set_df_property(DETAIL_TABLE_FIELD, "hidden", 1);
    },

    refresh(frm) {
        try {
            const grid = frm.fields_dict[DETAIL_TABLE_FIELD].grid;
            grid.df.cannot_add_rows = true;
            grid.wrapper.find('.grid-add-row').hide();
            prevent_auto_row_add_on_tab(frm, DETAIL_TABLE_FIELD);

            const $wrapper = frm.fields_dict[DETAIL_TABLE_FIELD].$wrapper;
            $wrapper.prev('.custom-table-header-box').remove();

            if (current_item_key) {
                const filtered_rows = frm.doc[DETAIL_TABLE_FIELD].filter(
                    row => row.reference_item === current_item_key
                );

                frm._all_detail_rows = frm.doc[DETAIL_TABLE_FIELD];
                frm.doc[DETAIL_TABLE_FIELD] = filtered_rows;

                frm.refresh_field(DETAIL_TABLE_FIELD);

                const $headerBox = $(`
                    <div class="custom-table-header-box" 
                          style="background:#f2f2f2;padding:10px 15px;margin:10px 0;
                          border-radius:8px;font-weight:bold;font-size:14px;">
                        ${current_item_key}
                     </div>
                `);
                $wrapper.before($headerBox);
            }
        } catch (e) {
            console.warn("detail-table refresh warning:", e);
        }

        add_injected_buttons(frm);
        setTimeout(() => add_injected_buttons(frm), 250);
        setTimeout(() => add_injected_buttons(frm), 900);

        if (!frm.__detail_btn_observer && frm.fields_dict[TABLE_FIELD] && frm.fields_dict[TABLE_FIELD].grid) {
            try {
                const wrapperNode = frm.fields_dict[TABLE_FIELD].grid.wrapper[0];
                if (wrapperNode) {
                    const observer = new MutationObserver(() => {
                        add_injected_buttons(frm);
                    });
                    observer.observe(wrapperNode, { childList: true, subtree: true });
                    frm.__detail_btn_observer = observer;
                }
            } catch (e) {
                console.warn("observer not created", e);
            }
        }

        // ðŸ‘‡ footer update on refresh
        update_detail_table_footer(frm);
    },

    validate(frm) {
        if (frm._all_detail_rows) {
            frm.doc[DETAIL_TABLE_FIELD] = frm._all_detail_rows;
            frm._all_detail_rows = null;
        }

        frm.doc[DETAIL_TABLE_FIELD] = frm.doc[DETAIL_TABLE_FIELD].filter(
            row => row.quantity !== null && row.reference_item
        );

        let counterMap = {};
        for (let row of frm.doc[DETAIL_TABLE_FIELD]) {
            const ref = row.reference_item;
            if (!counterMap[ref]) counterMap[ref] = 1;
            row.idx = counterMap[ref]++;
        }
    }
});

frappe.ui.form.on('Job Inward Item', {
    btn_detail(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        show_item_detail_for_row(frm, row);
    }
});

function add_injected_buttons(frm) {
    if (!frm.fields_dict[TABLE_FIELD] || !frm.fields_dict[TABLE_FIELD].grid) return;
    const $grid = $(frm.fields_dict[TABLE_FIELD].grid.wrapper);

    $grid.find('.grid-row').each(function () {
        const $row = $(this);
        const rowname = $row.attr('data-name') || $row.attr('data-idx');
        if (!rowname) return;

        if ($row.find(`button[data-fieldname="${EXISTING_BTN_FIELDNAME}"]`).length) {
            return;
        }

        if ($row.find(`.${INJECT_CLASS}[data-rowname="${rowname}"]`).length) return;

        let $cell = $row.find(`td[data-fieldname="${EXISTING_BTN_FIELDNAME}"]`);
        if (!$cell.length) $cell = $row.find('td').last();

        const $btn = $(`
            <button class="btn btn-xs btn-default ${INJECT_CLASS}" 
                     data-rowname="${rowname}" title="Detail" 
                     style="margin-left:6px;">Detail</button>
        `);
        $cell.append($btn);
    });

    $grid.off('click', `.${INJECT_CLASS}`).on('click', `.${INJECT_CLASS}`, function (e) {
        e.preventDefault();
        e.stopPropagation();
        const rowname = $(this).data('rowname');

        const child_rows = frm.doc[TABLE_FIELD] || [];
        let rowdoc = child_rows.find(r => (r.name && r.name == rowname) || (r.idx && r.idx == Number(rowname)));
        if (!rowdoc) {
            rowdoc = child_rows[Number(rowname) - 1] || child_rows[0];
        }
        if (!rowdoc) {
            frappe.msgprint(('Row not found.'));
            return;
        }

        show_item_detail_for_row(frm, rowdoc);
    });
}

function show_item_detail_for_row(frm, row) {
    if (!row.total_pcs || row.total_pcs <= 0) {
        frappe.msgprint("Total PCS must be greater than 0.");
        return;
    }
    if (!row.total_quantity || row.total_quantity <= 0) {
        frappe.msgprint("Total Quantity must be greater than 0.");
        return;
    }

    if (frm.doc.docstatus === 1) {
        const current_item_key_local = `${row.product_type || ""} - ${row.product_name || ""}`;
        const all_rows = frm._all_detail_rows || frm.doc[DETAIL_TABLE_FIELD] || [];
        const relevant_rows = all_rows.filter(r => r.reference_item === current_item_key_local);

        if (relevant_rows.length === 0) {
            frappe.msgprint(('No detail rows available for this item.'));
            return;
        }

        let html = `<div style="max-height:400px;overflow:auto">
            <table class="table table-bordered"><thead>
                <tr><th>#</th><th>Quantity</th><th>Reference Item</th></tr>
            </thead><tbody>`;

        relevant_rows.forEach((r, idx) => {
            html += `<tr>
                <td>${idx + 1}</td>
                <td>${(r.quantity || 0)}</td>
                <td>${frappe.utils.escape_html(r.reference_item || '')}</td>
            </tr>`;
        });

        html += `</tbody></table></div>`;

        const d = new frappe.ui.Dialog({
            title: __('Details for {0}', [current_item_key_local]),
            fields: [
                { fieldtype: 'HTML', fieldname: 'content' },
                { fieldtype: 'Button', label: 'Run Action', fieldname: 'run_action' }
            ]
        });

        d.fields_dict.content.$wrapper.html(html);

        d.fields_dict.run_action.$input.on('click', function () {
            frappe.msgprint((__('Action executed for {0}', [current_item_key_local])));
        });

        d.show();
        return;
    }

    current_item_key = `${row.product_type || ""} - ${row.product_name || ""}`;
    current_item_reference = {
        total_pcs: row.total_pcs,
        total_qty: row.total_quantity
    };

    const all_rows = frm._all_detail_rows || frm.doc[DETAIL_TABLE_FIELD] || [];
    const relevant_rows = all_rows.filter(r => r.reference_item === current_item_key);
    const remaining_rows = all_rows.filter(r => r.reference_item !== current_item_key);
    frm.doc[DETAIL_TABLE_FIELD] = remaining_rows;

    let rows_to_show = [];

    if (relevant_rows.length > 0) {
        rows_to_show = relevant_rows.slice(0, row.total_pcs);
        while (rows_to_show.length < row.total_pcs) {
            rows_to_show.push({ quantity: 0, reference_item: current_item_key });
        }
    } else {
        for (let i = 0; i < row.total_pcs; i++) {
            rows_to_show.push({ quantity: 0, reference_item: current_item_key });
        }
    }

    for (let i = 0; i < rows_to_show.length; i++) {
        const item = rows_to_show[i];
        const new_row = frm.add_child(DETAIL_TABLE_FIELD);
        new_row.quantity = item.quantity || 0;
        new_row.reference_item = current_item_key;
        new_row.idx = i + 1;
    }

    frm.set_df_property(DETAIL_TABLE_FIELD, "hidden", 0);
    frm.refresh_field(DETAIL_TABLE_FIELD);
    frm.trigger("refresh");

    // ðŸ‘‡ footer update after showing table
    update_detail_table_footer(frm);
}

frappe.ui.form.on('Job Inward Item Detail', {
    quantity(frm, cdt, cdn) {
        if (!current_item_reference) return;

        const rows = frm.doc[DETAIL_TABLE_FIELD].filter(r => r.reference_item === current_item_key);
        const total_entered = rows.reduce((sum, row) => sum + (row.quantity || 0), 0);
        const is_last_row = rows[rows.length - 1].name === cdn;
        const total_SubQty = rows.reduce((sum, r) => sum + (r.quantity || 0), 0);
        if (is_last_row && total_entered !== current_item_reference.total_qty) {
            frappe.msgprint(`âŒ Total Quantity must be exactly ${current_item_reference.total_qty}. You entered ${total_entered}.`);
        }
        //123456789
         if (total_SubQty > current_item_reference.total_qty) {
            frappe.msgprint(`LINE BY LINE âŒ Total Quantity must be exactly ${current_item_reference.total_qty}. You entered ${total_entered}.`);
        }

        // ðŸ‘‡ update footer live on change
        update_detail_table_footer(frm);
    },

    job_inward_item_detail_add(frm, cdt, cdn) {
        const rows = frm.doc[DETAIL_TABLE_FIELD] || [];
        const allowed_rows = current_item_reference?.total_pcs || 0;

//THIS SCRIPT IS FOR NO MORE THEN PCS IN SUBDETAIL VALIDATE
        if (rows.length > allowed_rows) {
            frm.get_field(DETAIL_TABLE_FIELD).grid.grid_rows[rows.length - 1].remove();
            // frappe.msgprint(`âš  You cannot add more than ${allowed_rows} rows.`);
        }
    }
});

function prevent_auto_row_add_on_tab(frm, table_fieldname) {
    setTimeout(() => {
        try {
            const grid = frm.fields_dict[table_fieldname].grid;
            const wrapper = $(grid.wrapper);

            wrapper.find('input').off('keydown.preventrowadd');

            wrapper.on('keydown.preventrowadd', 'input', function (e) {
                const isLastEditableInput = $(this).closest('tr').is(':last-child');
                const isTab = e.key === "Tab" || e.which === 9;

                if (isTab && isLastEditableInput) {
                    const total_rows = frm.doc[table_fieldname]?.filter(row => row.reference_item === current_item_key)?.length || 0;
                    const allowed_rows = current_item_reference?.total_pcs || 0;

                    if (total_rows >= allowed_rows) {
                        e.preventDefault();
                        $(this).blur();
                    }
                }
            });
        } catch (e) {}
    }, 300);
}

// ---------- NEW FUNCTION ---------- SUB DETAIL UNDER TOTAL QUANTI
function update_detail_table_footer(frm) {
    try {
        const $wrapper = frm.fields_dict[DETAIL_TABLE_FIELD].$wrapper;

        // purana footer hatado
        $wrapper.next('.custom-table-footer-box').remove();

        if (current_item_key) {
            const rows = frm.doc[DETAIL_TABLE_FIELD].filter(
                r => r.reference_item === current_item_key
            );
            const total_qty = rows.reduce((sum, r) => sum + (r.quantity || 0), 0);

            const $footerBox = $(`
                <div class="custom-table-footer-box"
                     style="background:#f9f9f9;padding:10px 15px;margin:10px 0;
                            border-radius:8px;font-weight:bold;font-size:14px;
                            text-align:right;">
                     Total Quantity: ${total_qty}
                </div>
            `);

            $wrapper.after($footerBox);
        }
    } catch (e) {
        console.warn("update footer failed:", e);
    }
}

