frappe.listview_settings['Lead'] = {
    onload(listview) {
        listview.page.add_inner_button('Edit Lead Status', async () => {
            const selected_leads = listview.get_checked_items();

            if (selected_leads.length !== 1) {
                frappe.msgprint(('Please select exactly one Lead'));
                return;
            }

            const lead_name = selected_leads[0].name;
            const lead_data = await frappe.db.get_doc("Lead", lead_name);

            const dialog = new frappe.ui.Dialog({
                title: 'Edit Lead Status',
                fields: [
                    {
                        fieldname: 'custom_lead_status_tag',
                        label: 'Lead Status Tag',
                        fieldtype: 'Select',
                        options: [
                            "Required Later",
                            "Not Required",
                            "Send Catalogue",
                            "Send Sample",
                            "Send Quotation",
                            "Advance Received",
                            "Received Design From Party",
                            "Design Approved",
                            "Date Received"
                        ].join('\n'),
                        reqd: 1,
                        default: lead_data.custom_lead_status_tag || ""
                    },
                    {
                        fieldname: 'custom_call_again',
                        label: 'Call Again Date',
                        fieldtype: 'Date',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Required Later"',
                        default: lead_data.custom_call_again || ""
                    },
                    {
                        fieldname: 'custom_reason',
                        label: 'Reason',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Not Required"',
                        default: lead_data.custom_reason || ""
                    },
                    {
                        fieldname: 'custom_send_catalogue_email_id',
                        label: 'Send Catalogue Email ID',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Send Catalogue"',
                        default: lead_data.custom_send_catalogue_email_id || ""
                    },
                    {
                        fieldname: 'custom_send_catalogue_whatsapp_no',
                        label: 'Send Catalogue Whatsapp No',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Send Catalogue"',
                        default: lead_data.custom_send_catalogue_whatsapp_no || ""
                    },
                    {
                        fieldname: 'custom_sample_name',
                        label: 'Sample Name',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Send Sample"',
                        default: lead_data.custom_sample_name || ""
                    },
                    {
                        fieldname: 'custom_send_sample_mobile_no',
                        label: 'Send Sample Mobile No',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Send Sample"',
                        default: lead_data.custom_send_sample_mobile_no || ""
                    },
                    {
                        fieldname: 'custom_delivery_address',
                        label: 'Delivery Address',
                        fieldtype: 'Small Text',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Send Sample"',
                        default: lead_data.custom_delivery_address || ""
                    },
                    {
                        fieldname: 'custom_date_received',
                        label: 'Date Received',
                        fieldtype: 'Date',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Advance Received"',
                        default: lead_data.custom_date_received || ""
                    },
                    {
                        fieldname: 'custom_amount_received',
                        label: 'Amount Received',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Advance Received"',
                        default: lead_data.custom_amount_received || ""
                    },
                    {
                        fieldname: 'custom_quotation_table',
                        label: 'Quotation Table',
                        fieldtype: 'Table',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Send Quotation"',
                        options: 'Custom Send Quotation Table Fields',
                        fields: [
                            {
                                fieldtype: 'Link',
                                label: 'Item',
                                fieldname: 'custom_item',
                                options: 'Item',
                                reqd: 1,
                                in_list_view: 1
                            },
                            {
                                fieldtype: 'Data',
                                label: 'Price',
                                fieldname: 'custom_price',
                                reqd: 1,
                                in_list_view: 1
                            },
                            {
                                fieldtype: 'Data',
                                label: 'Quantity',
                                fieldname: 'custom_quantity',
                                reqd: 1,
                                in_list_view: 1
                            }
                        ]
                    },
                    {
                        fieldname: 'custom_upload_file',
                        label: 'Upload File',
                        fieldtype: 'Attach',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Received Design From Party"',
                        default: lead_data.custom_upload_file || ""
                    },
                    {
                        fieldname: 'custom_send_file_to_mobile_no',
                        label: 'Send File To Mobile No',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Received Design From Party"',
                        default: lead_data.custom_send_file_to_mobile_no || ""
                    },
                    {
                        fieldname: 'custom_send_file_to_email',
                        label: 'Send File To Email',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Received Design From Party"',
                        default: lead_data.custom_send_file_to_email || ""
                    },
                    {
                        fieldname: 'custom_upload_final_photo_',
                        label: 'Upload Final Photo',
                        fieldtype: 'Attach',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Design Approved"',
                        default: lead_data.custom_upload_final_photo_ || ""
                    },
                    {
                        fieldname: 'custom_send_final_file_to_mobile_no',
                        label: 'Send Final File To Mobile No',
                        fieldtype: 'Data',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Design Approved"',
                        default: lead_data.custom_send_final_file_to_mobile_no || ""
                    },
                    {
                        fieldname: 'custom_receive_date',
                        label: 'Receive Date',
                        fieldtype: 'Date',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Date Received"',
                        default: lead_data.custom_receive_date || ""
                    },
                    {
                        fieldname: 'custom_receive_photo',
                        label: 'Receive Photo',
                        fieldtype: 'Attach',
                        depends_on: 'eval:doc.custom_lead_status_tag === "Date Received"',
                        default: lead_data.custom_receive_photo || ""
                    }
                ],
                primary_action_label: 'Update',
                primary_action: async function (values) {
                    await frappe.call({
                        method: 'frappe.client.set_value',
                        args: {
                            doctype: 'Lead',
                            name: lead_name,
                            fieldname: {
                                custom_lead_status_tag: values.custom_lead_status_tag,
                                custom_call_again: values.custom_call_again,
                                custom_reason: values.custom_reason,
                                custom_send_catalogue_email_id: values.custom_send_catalogue_email_id,
                                custom_send_catalogue_whatsapp_no: values.custom_send_catalogue_whatsapp_no,
                                custom_sample_name: values.custom_sample_name,
                                custom_send_sample_mobile_no: values.custom_send_sample_mobile_no,
                                custom_delivery_address: values.custom_delivery_address,
                                custom_date_received: values.custom_date_received,
                                custom_amount_received: values.custom_amount_received,
                                custom_upload_file: values.custom_upload_file,
                                custom_send_file_to_mobile_no: values.custom_send_file_to_mobile_no,
                                custom_send_file_to_email: values.custom_send_file_to_email,
                                custom_upload_final_photo_: values.custom_upload_final_photo_,
                                custom_send_final_file_to_mobile_no: values.custom_send_final_file_to_mobile_no,
                                custom_receive_date: values.custom_receive_date,
                                custom_receive_photo: values.custom_receive_photo
                            }
                        }
                    });

                    if (values.custom_lead_status_tag === "Send Quotation") {
                        await frappe.call({
                            method: "frappe.client.set_value",
                            args: {
                                doctype: "Lead",
                                name: lead_name,
                                fieldname: { custom_quotation_table: [] }
                            }
                        });

                        if (Array.isArray(values.custom_quotation_table)) {
                            for (const row of values.custom_quotation_table) {
                                await frappe.call({
                                    method: "frappe.client.insert",
                                    args: {
                                        doc: {
                                            doctype: "Custom Send Quotation Table Fields",
                                            parent: lead_name,
                                            parenttype: "Lead",
                                            parentfield: "custom_quotation_table",
                                            custom_item: row.custom_item,
                                            custom_price: row.custom_price,
                                            custom_quantity: row.custom_quantity
                                        }
                                    }
                                });
                            }
                        }
                    }

                    frappe.msgprint('Lead updated successfully');
                    dialog.hide();
                    listview.refresh();
                }
            });

            dialog.fields_dict.custom_lead_status_tag.df.onchange = () => {
                dialog.refresh();
            };

            dialog.show();
            dialog.fields_dict.custom_lead_status_tag.df.onchange();
            dialog.refresh();

            if (lead_data.custom_lead_status_tag === "Send Quotation") {
                const rows = await frappe.db.get_list('Custom Send Quotation Table Fields', {
                    filters: {
                        parent: lead_name,
                        parenttype: 'Lead',
                        parentfield: 'custom_quotation_table'
                    },
                    fields: ['custom_item', 'custom_price', 'custom_quantity']
                });

                if (Array.isArray(rows)) {
                    dialog.fields_dict.custom_quotation_table.df.data = rows;
                    dialog.fields_dict.custom_quotation_table.grid.df.data = rows;
                    dialog.fields_dict.custom_quotation_table.grid.refresh();
                }
            }
        });
    }
};
